svg.script:
var sw_d121 = {

  roll_handler: function(evt) {
    if (this.isChanging) {
      return false
    }
    this.isChanging = true
    var origOpacity = this.style.opacity
    animated_ghost(this, { animation: 'rollOut' })
    this.faces.forEach((name) => {
      gEl = this.getElementById(name)
      gEl.style.setProperty('display', 'none')
    })
    this.style.opacity = 0.1
    animated_ghost(this, {
      animation: 'rollIn',
      before_begin: (ghost) => {
        ghost.querySelector('g').style.opacity = origOpacity
      },
      on_done: () => {
        var newNum = randInt(1,12)
        if (newNum !== 12) {
           activeFace = this.faces[newNum-1]
           gEl = this.getElementById(activeFace)
           gEl.setAttribute('display', '')
           gEl.style.setProperty('display', '')
        }

        this.style.opacity = origOpacity
        this.isChanging = false
        net_fire({type: "change", data: serialize(this)});
      },
    })
  },

  initialize: function(elem) {
    elem.faces = [
      'g_oct', 'g_oct', 'g_octoct', 'g_octoct',
      'g_tri', 'g_tri', 'g_tritri', 'g_tritri',
      'g_trioct', 'g_trioct',
      'g_ctri',
    ]

    elem.addEventListener('die_roll', this.roll_handler)
    elem.addEventListener('dblclick', this.roll_handler)
  },

  menu: {
    'Roll': {
      eventName: 'die_roll',
      applicable: (dieNode) => { return true },
    },
  },

  serialize: function(elem) {
    value = elem.querySelector('tspan').textContent
    return {dieValue: value}
  },

  deserialize: function(elem, payload) {
    elem.querySelector('tspan').textContent = payload.dieValue
  },

}
