svg.script:
var drag_select_box = {

  paper_roll_handler: function(evt) {
    return paper.roll_handler(this, evt)
  },

  initialize: function(elem, prototype) {
    elem.addEventListener('selection_roll', this.paper_roll_handler)
    elem.addEventListener('resize', this.resize_handler)
  },

  resize_handler: function(evt) {
    paper.resize_handler(this, evt)
  },

  menu: {
    'Roll All': {
      eventName: 'selection_roll',
      applicable: (node) => { return true },
    },
  },

}

