svg.script:
var pbta_dice = {
  roll_handler: function(evt) {
    if (this.isChanging) {
      return false
    }
    this.isChanging = true
    var text = this.querySelector('text')
    var tspan = this.querySelector('tspan')
    var origOpacity = this.style.opacity
    var newNum = randDiceString(1,6)
    animated_ghost(this, { animation: 'rollOut' })
    tspan.textContent = ' '
    this.style.opacity = 0.1
    animated_ghost(this, {
      animation: 'rollIn',
      before_begin: (ghost) => {
        ghost.querySelector('g').style.opacity = origOpacity
      },
      on_done: () => {
        tspan.textContent = newNum
        s(text, 'data-text', newNum)
        this.style.opacity = origOpacity
        this.isChanging = false
        net_fire({type: "change", data: serialize(this)})
        evt_fire('change', this, null, {})
      },
    })
  },

  initialize: function(elem) {
    elem.addEventListener('die_roll', this.roll_handler)
    elem.addEventListener('dblclick', this.roll_handler)
  },

  menu: {
    'Roll': {
      eventName: 'die_roll',
      applicable: (dieNode) => { return true },
    },
  },

  serialize: function(elem) {
    value = elem.querySelector('tspan').textContent
    return {dieValue: value}
  },

  deserialize: function(elem, payload) {
    var tspan = elem.querySelector('tspan')
    tspan.textContent = payload.dieValue
  },

}

var pbta_dice = {
  roll_handler: function(evt) {
    console.log('pbta roll handler')
    die1 = pbta_dice.select_my(this, 'pbta_die_1')
    evt_fire('die_roll', die1, null, {})
  },

  initialize: function(elem) {
    elem.addEventListener('die_roll', this.roll_handler)
    elem.addEventListener('dblclick', this.roll_handler)
    pbta_dice.initialize(elem.querySelector('#pbta_die_1')
    pbta_dice.initialize(elem.querySelector('#pbta_die_2')

    var idStrings = [
      'pbta_die_1',
      'pbta_die_2',
    ]
    idStrings.forEach((idString) => {
      el = elem.querySelector('#' + idString)
      el.id = elem.id + '_' + idString
    })

  },

  select_my: function(parentElem, oldId) {
    q = '#' + parentElem.id + '_' + oldId
    return parentElem.querySelector(q)
  },

  menu: {
    'Roll': {
      eventName: 'die_roll',
      applicable: (dieNode) => { return true },
    },
  },

  serialize: function(elem) {
    console.log('pbta serialize')
  },
}
