svg.script:
var d8 = {

  roll_handler: function(evt) {
    console.log('d8 ', this, 'roll!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!', evt.detail)
    if (this.id !== evt.detail.elemId) {
      return;
    }
    var text = this.querySelector('text')
    var tspan = this.querySelector('tspan')
    var origOpacity = this.style.opacity
    var newNum = randDiceString(1,8)
    animated_ghost(this, { animation: 'rollOut' })
    tspan.textContent = ' '
    this.style.opacity = 0.1
    animated_ghost(this, {
      animation: 'rollIn',
      before_begin: (ghost) => {
        ghost.querySelector('g').style.opacity = origOpacity
      },
      on_done: () => {
        tspan.textContent = newNum
        s(text, 'data-text', newNum)
        this.style.opacity = origOpacity
        net_fire({type: "change", data: serialize(this)});
      },
    })
  },

  turn_handler: function(evt) {
    console.log('d8 ', this, 'turn!!!!!!!!!!!!!!!!!!!!!!!!!!!!', evt.detail)
    if (this.id !== evt.detail.elemId) {
      return;
    }
    var text = this.querySelector('text')
    var tspan = this.querySelector('tspan')
    var origOpacity = this.style.opacity
    var origNum = parseInt(tspan.textContent)
    animated_ghost(this, {
      animation: 'thumb',
      on_done: () => {
        tspan.textContent = (origNum % 8) + 1
        s(text, 'data-text', (origNum % 8) + 1)
        this.style.opacity = origOpacity
        net_fire({type: "change", data: serialize(this)});
      },
    })
    tspan.textContent = " "
    this.style.opacity = 0.1
  },

  hookup_handlers: function(elem) {
    console.log('hooking up handlers for ', elem)
    elem.addEventListener('die_roll', this.roll_handler)
    elem.addEventListener('die_turn_up', this.turn_handler)
  },

  v1_menu: {
    'Roll': {
      eventName: 'die_roll',
      applicable: (dieNode) => { return true },
    },
    'Turn Up': {
      eventName: 'die_turn_up',
      applicable: (dieNode) => { return true },
      uiLabel: (dieNode) => { 
        var tspan = dieNode.querySelector('tspan')
        var nextNum = (parseInt(tspan.textContent) % 8) + 1
        return 'Turn to ' + nextNum
        },
    },
  },
}

console.log('d8 script has run')
